mapper.select:
SELECT COUNT(*) FROM AUCTION_LIKE WHERE AUCTION_NO =
#{no} AND USERNO =
#{userno}

SELECT * FROM TOWN_LIST L 
LEFT OUTER JOIN MEMBERS M ON L.USERNO = M.USERNO
WHERE (SELECT SIMPLEADDRESS FROM ADDRESS_LIST A WHERE L.ADDRNO = A.ADDRNO) = #{simpleAddress}

SELECT COUNT(*) FROM AUCTION_LIST L
LEFT OUTER JOIN AUCTION_PRICE P ON L.AUCTION_NO = P.AUCTION_NO
WHERE L.STATUS = 'END' AND P.UPPER_USER_NO = #{userno}

mapper.update:
UPDATE AUCTION_LIST
SET TITLE=#{title}, CONTENT=#{content},
CATEGORY=#{category}, LIKES=0,
POSTDATE=SYSDATE, ADDRNO=#{addrno}
WHERE AUCTION_NO = #{auction_no}

UPDATE
AUCTION_PRICE SET BASE_PRICE=#{price}, UPPER_PRICE=#{price}
WHERE
AUCTION_NO = #{auction_no}

mapper.select:
SELECT L.*, ROWNUM FROM ADDRESS_LIST L
WHERE USERNO = #{userno}
ORDER BY ADDRNO ASC
